import React from 'react';
import './Hero.scss';
import posterVideo from '../../assets/Images/video-list-0.jpg' 
import videoPlay from '../../assets/Icon/Icon-play.svg';
import videoFull from '../../assets/Icon/Icon-fullscreen.svg';
import videoSound from '../../assets/Icon/Icon-volume.svg';


const Hero = () => {
    return (
        <div className='hero'>
            <video poster={posterVideo}>
            </video>
            {/* <div className='controls'>
                
                <div id='videoProcess' id='videobar'></div>
                <div id='side'><img src={videoFull} /><img src={videoSound} /></div>
            </div> */}
            <div id="video-controls" class="controls" data-state="hidden">
                <button id="playpause" type="button"><img src={videoPlay} id='play'/></button>
                <div class="progress">
                <progress id="progress" value="0" min="0">
                    <span id="progress-bar"></span>
                </progress>
            </div>
            </div>
        </div>
    );
  }

export default Hero;
