import React from 'react';
import './Main.scss';
import videoListsData from '../../videoListsData';
import videoDetailData from '../../videoDetailData';
import PlayingVideo from './PlayingVideo';
import ListsVideo from './ListsVideo';


class Main extends React.Component {
    state = {
      videoDetail: videoDetailData,

      onVideo: videoListsData[0],
      videoList: videoListsData
    }

    render() {

      return (
        <>
        <PlayingVideo
          poster={this.state.videoDetail[0].image}
          videoSample={this.state.videoDetail[0].video}
          comments={this.state.videoDetail[0].comments}
          title={this.state.videoDetail[0].channel}
          description={this.state.videoDetail[0].description}
          views={this.state.videoDetail[0].views}
          likes={this.state.videoDetail[0].likes}
          timestamp={this.state.videoDetail[0].timestamp}
        />
      
        
        <ListsVideo 
          nextVideos={this.state.videoList.filter(data => data !== this.state.onVideo)}
        />

        </>
          )
    };  
  }

export default Main;
